import React, { useEffect, useState } from 'react';

const SelectCoin = () => {

    const [coins,getCoin] = useState([]);



    useEffect(()=>{

    const coinSelected = (e) => {
        console.log(e);
        randomCoin();

    };

    const randomCoin = () => {
    fetch('https://api.upbit.com/v1/market/all')
    .then(res => res.json())
    .then(async res => {
             const coinList =await res.map(coin => coin.korean_name);
             const selected = coinList[Math.floor(Math.random() * coinList.length)]
             getCoin(selected);
    })
    };

    },[])

    return(
        <>
        <button onClick={coinSelected}>코인랜덤뽑기</button>
        <div>{coins}</div>
        </>
    )


};

export default SelectCoin;
